mapper.select:
SELECT br.blood_id, do.order_no || '_' || do.order_sub_no || '_' || do.patient_id || '_' || do.visit_id AS order_no, do.order_no AS group_no, do.patient_id, do.visit_id AS series, ba.inp_no AS admission_id, ba.PAT_NAME AS patient_name, ba.PAT_SEX, to_char(ba.birthday, 'yyyy-MM-dd hh24:mi:ss') as birthday, pih.bed_no, pih.ward_code, to_char(bos.blood_out_date, 'yyyy-MM-dd hh24:mi:ss') as blood_out_date, to_char(bm.match_date, 'yyyy-MM-dd hh24:mi:ss') as match_date, to_char(bm.INVAL_DATE, 'yyyy-MM-dd hh24:mi:ss') as INVAL_DATE, to_char(bc.trans_date, 'yyyy-MM-dd hh24:mi:ss') as trans_date, ba.BLOOD_DIAGNOSE, ba.pat_blood_group, ba.rh, (SELECT bco.blood_type_name FROM BLDBANK.blood_component bco WHERE bco.blood_type = bc.blood_type) AS blood_type_name, br.blood_capacity, br.blood_unit, br.blood_group FROM ORDADM.doctor_orders do, BLDBANK.blood_apply ba, INPADM.pats_in_hospital pih, MEDREC.pat_master_index pmi, BLDBANK.blood_out_sheet bos, BLDBANK.blood_match bm, BLDBANK.blood_capacity bc, BLDBANK.blood_rec br
and br.blood_out_id=#{bloodOutId,jdbcType=VARCHAR}
and br.blood_product_id=#{bloodProductId,jdbcType=VARCHAR}
and do.req_id = ba.apply_num AND pih.patient_id = do.patient_id AND pih.visit_id = do.visit_id AND pmi.patient_id = do.patient_id AND bos.apply_num = ba.apply_num AND ba.apply_num = bm.apply_num AND bc.apply_num = ba.apply_num AND br.apply_num = ba.apply_num AND bm.blood_id = bos.blood_id AND br.blood_id = bm.blood_id AND bos.match_sheet_num = bm.match_sheet_num

SELECT br.blood_out_num, br.blood_out_id, br.blood_product_id, br.blood_group, br.rh, br.blood_type, bc.blood_type_name, br.blood_capacity, br.blood_unit, bm.match_result FROM BLDBANK.blood_rec br, BLDBANK.blood_component bc, BLDBANK.blood_match bm
and br.blood_out_num=#{bloodOutNum,jdbcType=VARCHAR}
AND br.blood_type = bc.blood_type (+) AND br.blood_id = bm.blood_id AND br.apply_num = bm.apply_num
