mapper.select:
SELECT im.order_no, im.GROUP_NO, im.ORDER_SUB_NO, im.ORDER_CODE, im.DRUG_NAME, im.ORDER_NAME, im.PATIENT_ID, im.SERIES, im.ADMISSION_ID, im.DEPT_CODE, im.WARD_CODE, im.FREQUENCY_CODE, im.DOSAGE, im.DOSAGE_UNIT, im.SUPPLY_CODE, im.SUPPLY_NAME, im.ORDER_STATUS, im.ORDER_CLASS, im.ORDER_CLASS_NAME, im.LONG_ONCE_FLAG, im.START_TIME, im.ENTER_TIME, im.STOP_TIME, im.DOCTOR_NAME, im.STOP_DOCTOR_NAME, im.drug_spec, im.baby_no from ip_mnis_order_view im INNER JOIN pats_in_hospital pi on (pi.patient_id=im.PATIENT_ID and pi.visit_id=im.SERIES and pi.ward_code=#{wardCode,jdbcType=VARCHAR})
and im.GROUP_NO=#{orderNo,jdbcType=VARCHAR}
and im.order_sub_no=#{orderSubNo,jdbcType=VARCHAR}
AND
#{olItems,jdbcType=VARCHAR}
AND
#{scItems,jdbcType=VARCHAR}
and im.patient_id=#{patientId,jdbcType=VARCHAR} and im.series=#{series,jdbcType=VARCHAR}
and im.long_once_flag=#{longOnceFlag,jdbcType=VARCHAR}
and im.ORDER_STATUS=#{orderStatus,jdbcType=VARCHAR}
and im.ward_code=#{wardCode,jdbcType=VARCHAR}
and im.start_time>=to_date(#{startTime,jdbcType=VARCHAR}||'00:00:00','yyyy-MM-dd hh24:mi:ss')
and im.start_time<=to_date(#{endTime,jdbcType=VARCHAR}||'23:59:59','yyyy-MM-dd hh24:mi:ss')

SELECT im.order_no, im.GROUP_NO, im.ORDER_SUB_NO, im.ORDER_CODE, im.DRUG_NAME, im.ORDER_NAME, im.PATIENT_ID, im.SERIES, im.ADMISSION_ID, im.DEPT_CODE, im.WARD_CODE, im.FREQUENCY_CODE, im.DOSAGE, im.DOSAGE_UNIT, im.SUPPLY_CODE, im.SUPPLY_NAME, im.ORDER_STATUS, im.ORDER_CLASS, im.ORDER_CLASS_NAME, im.LONG_ONCE_FLAG, im.START_TIME, im.ENTER_TIME, im.STOP_TIME, im.DOCTOR_NAME, im.STOP_DOCTOR_NAME, im.drug_spec, im.baby_no from ip_mnis_drug_order_view im INNER JOIN pats_in_hospital pi on (pi.patient_id=im.PATIENT_ID and pi.visit_id=im.SERIES and pi.ward_code=#{wardCode,jdbcType=VARCHAR})
and im.patient_id=#{patientId,jdbcType=VARCHAR}
and im.series=#{series,jdbcType=VARCHAR}
and im.long_once_flag=#{longOnceFlag,jdbcType=VARCHAR}
and im.ORDER_STATUS='2' --and (im.supply_name like '%输液%' or im.supply_name like '%注射%') AND im.supply_code in ('P1','X5','BA','BB','BC','C4','H2','H3','H4','I2','F7','J8','J9','L2','L3','L5','L6','L7','M4','M5','M6','M7','N1','O2','O5','A2','F4','A4','B1','B2','B3','B4','B5','F1','F2','F5','F6','K8','F3','A3','D4','O7','X3','X7','O8','F8')

mapper.update:
UPDATE
orders


        STOP_NURSE=#{stopNurseId,jdbcType=VARCHAR},


        ORDER_STATUS=#{orderStatus,jdbcType=VARCHAR},


        STOP_DATE_TIME=to_date(#{stopOrderDateTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss'),


        LAST_PERFORM_DATE_TIME=to_date(#{updateTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss'),


        performed_by=#{updateBy,jdbcType=VARCHAR},




        AND patient_id=#{patientId,jdbcType=VARCHAR}


        AND VISIT_ID=#{series,jdbcType=VARCHAR}


        AND ORDER_NO=#{orderNo,jdbcType=VARCHAR}


        AND ORDER_SUB_NO=#{groupNo,jdbcType=VARCHAR}

mapper.select:
SELECT o.patient_id || '_' || o.visit_id AS req_no, o.patient_id, o.visit_id, o.order_no || '_' || o.order_sub_no AS doc_adv_no, o.order_text, to_char(o.start_date_time, 'yyyy-MM-dd hh24:mi:ss') as start_date_time, (CASE WHEN o.stop_date_time IS NULL AND o.repeat_indicator = '1' THEN '1900-01-01 00:00:00' WHEN o.stop_date_time IS NULL AND o.repeat_indicator = '0' THEN to_char(o.start_date_time,'yyyy-MM-dd hh24:mi:ss') ELSE to_char(o.stop_date_time, 'yyyy-MM-dd hh24:mi:ss') END) AS stop_date_time, o.administration, o.dosage, o.dosage_units, decode(o.repeat_indicator, 1, '长期', 0, '临时') AS repeat_indicator, o.doctor, o.ordering_dept FROM orders o
AND patient_id=#{patientId,jdbcType=VARCHAR}
AND VISIT_ID=#{visitId,jdbcType=VARCHAR}
and start_time >=to_date(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd')
and start_time <to_date(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd')

SELECT o.patient_id || '_' || o.visit_id AS req_no, o.patient_id, o.visit_id, o.order_no || '_' || o.order_sub_no AS order_no, o.order_code, o.order_text, o.administration, o.dosage, o.dosage_units, to_char(o.start_date_time,'yyyy-MM-dd hh24:mi:ss') as start_date_time, (CASE WHEN o.stop_date_time IS NULL AND o.repeat_indicator = '1' THEN '1900-01-01 00:00:00' WHEN o.stop_date_time IS NULL AND o.repeat_indicator = '0' THEN to_char(o.start_date_time,'yyyy-MM-dd hh24:mi:ss') ELSE to_char(o.stop_date_time, 'yyyy-MM-dd hh24:mi:ss') END) AS stop_date_time, decode(o.REPEAT_INDICATOR,'1','长期','0','临时','其他') as REPEAT_INDICATOR, o.FREQUENCY, o.DOCTOR, o.ORDERING_DEPT FROM orders o
AND o.patient_id=#{patientId,jdbcType=VARCHAR}
AND o.VISIT_ID=#{visitId,jdbcType=VARCHAR}
and o.start_date_time >=to_date(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd')
and o.start_date_time <to_date(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd')
AND o.order_class = 'A' AND substr(o.order_code, 0, 2) IN ('01', '02')
