mapper.select:
select pih.patient_id, pih.visit_id, pih.patient_id || '_' || pih.visit_id as admission_id, pmi.name, pmi.sex, pih.dept_code, (select dd.dept_name from dept_dict dd where pih.dept_code = dd.dept_code ) as dept_name, pih.ward_code, (select dd.dept_name from dept_dict dd where dd.dept_code = pih.ward_code) as ward_name, pih.bed_no, to_char(pmi.date_of_birth, 'yyyy-MM-dd') as birthday, pmi.phone_number_home as phone, pv.OCCUPATION, pmi.mailing_address as address, to_char(pih.admission_date_time, 'yyyy-MM-dd hh24:mi:ss') as admission_time, to_char(pih.adm_ward_date_time, 'yyyy-MM-dd hh24:mi:ss') as admission_ward_time, to_char(pv.discharge_date_time, 'yyyy-MM-dd hh24:mi:ss') as discharge_time, pih.diagnosis, pih.nursing_class, pih.patient_condition, pv.insurance_type as charge_type, pih.total_costs, pih.prepayments as pre_payment, pih.total_charges as self_payment, decode(pih.settled_indicator, '0', '1', '1', null) as arrear_flag, pv.attending_doctor as doctor_name, pv.isalergy as allergy from pats_in_hospital pih, pat_master_index pmi, pat_visit pv
and pmi.patient_id = pih.patient_id and pv.patient_id = pih.patient_id and pv.visit_id = pih.visit_id
and pih.patient_id=#{patientId,jdbcType=VARCHAR}
and pih.visit_id=#{visitId,jdbcType=VARCHAR}
and pih.ward_code=#{wardCode,jdbcType=VARCHAR}

SELECT pih.patient_id || '_' || pih.visit_id AS req_no, pih.patient_id, pih.visit_id, pmi.name, pmi.sex, pmi.id_no, to_char(pmi.date_of_birth, 'yyyy-MM-dd') AS birthday, pih.dept_code, pih.ward_code, pih.bed_no, pih.doctor_in_charge, to_char(pih.admission_date_time, 'yyyy-MM-dd hh24:mi:ss') AS admission_date_time, to_char(pv.discharge_date_time, 'yyyy-MM-dd hh24:mi:ss') AS discharge_date_time, pv.dept_admission_to AS in_hos_dept, dvw.ward_code AS in_hos_dist, pmi.phone_number_home, pmi.mailing_address, pmi.inp_no FROM pat_master_index pmi, pats_in_hospital pih, pat_visit pv, dept_vs_ward dvw
and pmi.patient_id = pih.patient_id AND pmi.patient_id = pv.patient_id AND pih.visit_id = pv.visit_id AND dvw.dept_code = pv.dept_admission_to
AND pih.patient_id=#{patientId,jdbcType=VARCHAR}
AND pih.VISIT_ID=#{visitId,jdbcType=VARCHAR}
AND pih.dept_code=#{currentDept,jdbcType=VARCHAR}
AND pih.ward_code=#{currentDist,jdbcType=VARCHAR}
and pih.admission_date_time >=to_date(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd')
and pih.admission_date_time <to_date(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd')
